#include <bits/stdc++.h>
#define int long long
#define M 1000000007
using namespace std;
int solve()
{
    int n;
    cin >> n;
    int arr[n];
    for (int i = 0; i < n; i++) {
        cin >> arr[i];
    }
    sort(arr, arr + n);
    int ans = 0, p = 1, q = 2;
    for (int i = n-1; i >= 0; i--) {
        ans=(ans%M+((p%M)*(arr[i]%M))%M)%M;
        p=((p%M)*(q%M))%M;
        q++;
    }
    return ans;
}
int32_t main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    int t;
    cin >> t;
    while (t--) {
        cout << solve() << endl;
    }
    return 0;
}
