def sum(n):
	s = 0
	while n > 0:
		s, n = s + n % 10, n // 10
	return s
def check(n):
	s = p = 0
	i = 2
	while n > 1:
		while n % i == 0:
			n, p = n // i, p + 1
		s += sum(i) * p
		p = 0
		i += 1
	return s

n = int(input())
if sum(n) == check(n):
	print(1)
else:
	print(0)
