#include <bits/stdc++.h>
#define ll long long int
#define pb push_back
#define fr(i,n) for(int i = 0; i < n; i++)
#define all(g) g.begin(),g.end()
#define rall(g) g.rbegin(),g.rend()
#define fi first
#define se second
using namespace std;

int main() {
    ll n,x;
    cin >> n;
    
    ll a[n];
    fr(i,n) cin >> a[i];
    
    vector<pair<ll,ll> > g;
    
    ll s = 0;
    fr(i,n){
        cin >> x;
        g.pb({x, a[i]});
        s += a[i];
    }
    
    sort(rall(g));
    ll j = 0;
    fr(i,n-1){
        s -= g[i].se;
        if(s < g[i].fi) j++;
    }
    
    if(j == n-1) cout << "Yes";
    else cout << "No";
}
