#include <bits/stdc++.h>
#define pb(x) push_back(x)
#define pp(x) pop_back()
#define lll long long int
using namespace std;
int main() {
    int t;
    cin >> t;
    for(;t-->0;){
        int n,x;
        cin >> n;
        vector<pair<int,bool>> vec;
        for(int i = 0; i < n; i++){
            cin >> x;
            vec.push_back(make_pair(x,true));
        }
    
        for(int i = 0; i < n; i++){
            cin >> x;
            vec.push_back(make_pair(vec[i].first+x,false));
        }
    
        sort(vec.begin(), vec.end(), [](const pair<int,bool> &a, const pair<int,bool> &b){
            if(a.first == b.first) return b.second;
            return a.first < b.first;
        });
        
        int count = 0;
        int m = 0;
        for(int i = 0; i < 2*n; i++){
            if(vec[i].second) count++;
            else count--;
            m = max(m,count);
        }
        
        cout << m << "\n";
    }
}
