#include<bits/stdc++.h>
#define int long long
using namespace std;
long long solve() {
    int n, k;
    cin >> n;
    int arr[n];
    for (int i = 0; i < n; i++)
        cin >> arr[i];
    cin >> k;
    sort(arr, arr + n);
    int sm = 0;
    int foo = arr[0];
    for (int i = 0; i < k; i++) {
        sm += (n * foo);
        foo = arr[i+1] - arr[i];
        n--;
    }
    return sm;
}
int32_t main()
{
    int t;
    cin >> t;
    while (t--)
        cout << solve() << '\n';
    return 0;
}
