#include <bits/stdc++.h>
using namespace std;

int count_pirime() {

    int n;
    scanf("%d", &n);
    
    if (n < 2)
        return -1;

    if (n % 7 == 0)
        return int(n / 7);

    if (n % 7 < 4 || n % 7 == 5)
        return int(n / 7 + 1);

    return int(n / 7 + 2);
}

int main()
{
    int t;
    scanf("%d", &t);

    while (t--)
        printf("%d\n", count_pirime());

    return 0;
}
