#include <bits/stdc++.h>
using namespace std;
long  a[102][10002], m = 1e9+7,n,q,w;
void solve(){
  for (int j = 1; j <101;j++)
  {
    a[j][0] = 1;
    for (int i = 1;i<10001;i++)
    {
      for( int k = 0;k<j & i-k>0;k++)
      {
        a[j][i] += a[j][i-1-k];
        a[j][i] = a[j][i] % m;
      }
    }
  }
}
int main()
{
  solve();
  cin >> n;
  for (int i =0;i<n;i++){
    cin >>q>>w;
    cout<<a[w][q]<<endl;
  }
}
