#include <bits/stdc++.h>
#define ll long long int
#define pb push_back
#define mk make_pair
#define fi first
#define se second
#define REP(i,n) for(int i = 0; i < n; i++)
#define REPS(i,n,t) for(int i = t; i < n; i+=t)
using namespace std;

int main(){
    int n,x;
    cin >> n >> x;
    ll a[n][2];
    ll res[n][2];
    
    for(int i = 0; i < n; i++) cin >> a[i][0];
    for(int i = 0; i < n; i++) cin >> a[i][1];
    
    res[0][0] = a[0][0];
    res[0][1] = a[0][1];
    for(int i = 1; i < n; i++) {
        res[i][0] = max(res[i-1][0],res[i-1][1]-x)+a[i][0];
        res[i][1] = max(res[i-1][0]-x,res[i-1][1])+a[i][1];
    }
    
    cout << max(res[n-1][0],res[n-1][1]);
}
