#include<iostream>
using namespace std;
const int M = 5002;
bool a[M];
int main()
{
	int n;
	scanf("%d",&n);
	int arr[n];
	for (int i=0; i<n; i++) {
		scanf("%d",&arr[i]);
	}
	long long s = 0;
	for (int i=0;i<n;i++) {
		long long c = 0;
		int t = 1;
		for (int j=i;j>=0;j--) {
			if (arr[j]+1 < M) {
				a[arr[j]] = 1;
			}
			for (int k=t; ; k++) {
				if (a[k] == false) {
					c += k;
					t = k;
					break;
				}
			}
		}
		s += c;
		for (int j = 0; j <= i; j++) {
			if (arr[j]+1<M)
			a[arr[j]] = 0;
		}
	}
	cout << s;
}
