#include<bits/stdc++.h>
#define int long long
#define M %1000000007
using namespace std;

int fib(int n)
{
  int a = 1, ta,
      b = 1, tb,
      c = 1, rc = 0,  tc,
      d = 0, rd = 1;

  while (n)
  {
    if (n & 1)
    {
      tc = rc;
      rc = rc*a M + rd*c M ;
      rd = tc*b M + rd*d M;
    }
    ta = a; tb = b; tc = c;
    a = a*a M  + b*c M;
    b = ta*b M + b*d M;
    c = c*ta M + d*c M;
    d = tc*tb M+ d*d M;

    n >>= 1;

  }
  return rc M;
}
int32_t main()
{
    int n;
    cin >> n;
	if (n % 2 == 1) n += 1;
    cout << fib(n);
}
