#include <iostream>
using namespace std;
const long long p=1e9+7;
long long f(long long n){
    long long r=1;
    for(int i=1; i<=n; i++){
        r=r*i%p;
    }
    return r;
}
long long binpow(long long a , long long n){
    long long r=1;
    while(n>0){
        if(n%2) r=r*a%p;
        n/=2;
        a=1LL*a*a%p;
    }
    return r;
}
long long o(long long b){
    return binpow(b , p-2);
}
int main()
{
    long long n , m;
    cin>>n>>m;
    cout<<f(m+n)*o(f(n)*f(m)%p)%p;
    return 0;
}//1111111111111111111118188181881818118188181818188181818181818
