//n < 6 -> 0
//
//x = n - 5
//a(x) = x * (x + 1) / 2 

#include<bits/stdc++.h>
#define int long long
using namespace std;
int32_t main()
{
    int n;
    cin >> n;
    if (n < 6) cout << 0;
    else {
        int x = n - 5;
        int an = x * (x + 1) / 2;
        cout << an;
    }
}
