#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
ll p[10000000]; 
int main()
{
  ll a,b;
  cin>>a>>b;

  ll t=sqrt(a);
  int cnt=0;
  ll q=t;
  for(;q>=1;q--)
     if(a%q==0) p[cnt++]=q; 
   sort(p,p+cnt); 
  ll k=sqrt(a+b);
  ll x=k,y;
  ll ans=0;
   for(;x>=1;x--) 
  {
    if((a+b)%x==0)
    {
       y=(a+b)/x; 
      int i;
      if(p[cnt-1]<x) i=cnt-1;
       else i=lower_bound(p,p+cnt,x)-p; 
      if(p[i]>x) i--;
      if(i==-1) continue;
      q=a/p[i];
       if(y>=q) 
      {
        ans=2*(x+y);
        break;
      }
    }
  }
  
  t=a;
  a=b;
  b=t;
   
  t=sqrt(a);
  cnt=0;
  q=t;
  for(;q>=1;q--)
    if(a%q==0) p[cnt++]=q;
  sort(p,p+cnt);
  k=sqrt(a+b);
  x=k;
  for(;x>=1;x--)
  {
    if((a+b)%x==0)
    {
      y=(a+b)/x;
      int i;
      if(p[cnt-1]<x) i=cnt-1;
      else i=lower_bound(p,p+cnt,x)-p;
      if(p[i]>x) i--;
      if(i==-1) continue;
      q=a/p[i];
      if(y>=q)
      {
         ans=min(ans,2*(x+y)); 
        break;
      }
    }
  }
  cout << ans;
}
