#include<bits/stdc++.h>
#define int long long
using namespace std;

const int MAX = 1000000;
bool prime[MAX] = {false};

void check_prime() {

    prime[0] = true;
    prime[1] = true;

    for (int i = 2; i * i <= MAX; i += 1) {
        if (!prime[i]) {
            for (int j = i * i; j <= MAX; j += i) {
                prime[j] = true;
            }
        }
    }
}

int32_t main()
{
    int n;
    cin >> n;

    check_prime();

    int curr = 0;

    for (int i = 2; i * i <= n; i += 1) {
        if (!prime[i]) {
            curr += 1;
        }
    }

    cout << curr;
    return 0;
}
