const int M = 1e9 + 7;

vector<int> fac(N), inv(N);

int binpow(int a, int n)

{

  int res = 1;
while(n)

  {

    if(n & 1)

      res = res * a % M;

    n >>= 1;

    a = a * a % M;

  }

  return res;

}

void solution()

{

  int n;

  int a, b, q;

  cin >> n >> q;

  vector<int> aa(q);
  int s = 0;

  for(int &x : aa)

  {

    cin >> x;

    s += x;

  }

  bool ar, ge;

  ar = ge = true;

  for(int i = 1; i + 1 < q; i ++)

  {

    if(aa[i] * 2 != aa[i - 1] + aa[i + 1])

      ar = false;

    if(aa[i] * aa[i] != aa[i - 1] * aa[i + 1])

      ge = false;

  }
  if(ar)

  {

    a = aa[0];

    b = aa[1];

    n += q;

    int d = b - a;

    int ans = n * (2 * a + (n - 1) * d) / 2;

    cout << (ans - s) % M;

  }

  else if(ge)

  {

    for(int x : aa)

    {

      if(x == 0)
      {
      	cout << "BILMAYMAN";

        return;

      }

    }

    a = aa[0];

    b = aa[1];

    n += q;

    int nom = (binpow(b, n) - binpow(a, n) + M) % M;
int denom = binpow((b - a) * binpow(a, n - 2) % M, M - 2);

    cout << (nom * denom % M - s + M) % M;

  }

  else

    cout << "BILMAYMAN";

}

