#include <bits/stdc++.h>
#define int long long
using namespace std;

void bin(int n) {
    stack<int>st;
    while (n > 0) {
        st.push(n % 2);
        n /= 2;
    }
    while (!st.empty()) {
        cout << st.top();
        st.pop();
    }
    cout << ' ';
}

void hex(int n) {
    stack<int>st;
    while (n > 0) {
        st.push(n % 8);
        n /= 8;
    }
    while (!st.empty()) {
        cout << st.top();
        st.pop();
    }
    cout << ' ';
}

void oct(int n) {
    stack<char>stt;
    while (n > 0) {
        int p = n % 16;
        n /= 16;
        if (p < 10) {
            stt.push(char(p + 48));
        }
        else {
            if (p == 10) {
                stt.push('a');
            }
            else {
                if (p == 11) {
                    stt.push('b');
                }
                else {
                    if (p == 12) {
                        stt.push('c');
                    }
                    else {
                        if (p == 13) {
                            stt.push('d');
                        }
                        else {
                            if (p == 14) {
                                stt.push('e');
                            }
                            else {
                                stt.push('f');
                            }
                        }
                    }
                }
            }
        }
    }
    while (!stt.empty()) {
        cout << stt.top();
        stt.pop();
    }
    cout << ' ';
}

void bit(int n) {
    string s = "";
    for (int i = 10; i < 36; i++) {
        s = s + char(i + 87);
    }

    stack<char>sst;
    while (n > 0) {
        int p = n % 36;
        if (p >= 10) {
            sst.push(s[p - 10]);
        }
        else {
            sst.push(char(p + 48));
        }
        n /= 36;
    }

    while (!sst.empty()) {
        cout << sst.top();
        sst.pop();
    }
    cout << ' ';

}

void solve() {

    int x;
    cin >> x;

    bin(x); hex(x);
    oct(x); bit(x);
}

int32_t main()
{
    int t;
    cin >> t;
    while (t--) {
        solve();
        cout << endl;
    }
    return 0;
}
