a, b, n = map(int,input().split())

if a < b:
    a, b = b, a
if a == 0 and b == 0 or a == 0 and n % b != 0 or b == 0 and n % a != 0:
    print(-1)
    exit()
xa = n // a
xb = 0
q = n % a

while xa > 0 and q != 0:
    xb += q//b
    q -= b*(q//b)
    if q == 0:
        break
    xa -= 1
    q += a

if q != 0:
    print(-1)
else:
    print(str(b) * xb + str(a) * xa)
