def solve(n):
	
	arr = [9, 8, 7, 6, 5, 4, 3, 2]
	if n == 1:
		return 1;
	
	curr = ''
	
	while n != 1 and len(arr) != 0:
		k = arr[0]
		arr.pop(0)
		while n % k == 0:
			n //= k
			curr = curr + str(k)
			
	if n != 1:
		return -1
		
	return curr[::-1]
		
print(solve(int(input())))
