#include <iostream>
#define int long long
using namespace std;
int z = 1000000007, p = 1;
int32_t main()
{
    int n, x, y;
    cin >> n;
    for (int i=0; i<n; i++)
    {
        cin >> x >> y;
        if (x>0&&y>0||x<0&&y<0)
        {
            p = p * (y - x + 1) % z;
        }
        else
        {
            p = p * (y - x) % z;
        }
    }
    cout << p;
  return 0;
}
