#include <bits/stdc++.h>
using namespace std;

int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    int n, t;
    cin >> n;
    string st = "";
    if (n > 999) {
        t = n / 1000;
        n = n % 1000;
        switch (t)
        {
            case 1 : st += "M"; break;
            case 2 : st += "MM"; break;
            case 3 : st += "MMM"; break;
        }
    }
    if (n > 99) {
        t = n / 100;
        n = n % 100;
        switch (t)
        {
            case 1: st += "C"; break;
            case 2: st += "CC"; break;
            case 3: st += "CCC"; break;
            case 4: st += "CD"; break;
            case 5: st += "D"; break;
            case 6: st += "DC"; break;
            case 7: st += "DCC"; break;
            case 8: st += "DCCC"; break;
            case 9: st += "CM"; break;
        }
    }
    if (n > 9) {
        t = n / 10;
        n = n % 10;
        switch (t)
        {
            case 1: st += "X"; break;
            case 2: st += "XX"; break;
            case 3: st += "XXX"; break;
            case 4: st += "XL"; break;
            case 5: st += "L"; break;
            case 6: st += "LX"; break;
            case 7: st += "LXX"; break;
            case 8: st += "LXXX"; break;
            case 9: st += "XC"; break;
        }
    }
    if (n > 0) {
        switch (n)
        {
            case 1: st += "I"; break;
            case 2: st += "II"; break;
            case 3: st += "III"; break;
            case 4: st += "IV"; break;
            case 5: st += "V"; break;
            case 6: st += "VI"; break;
            case 7: st += "VII"; break;
            case 8: st += "VIII"; break;
            case 9: st += "IX"; break;
        }
    }
    cout << st;
    return 0;
}
