#include <bits/stdc++.h>
#define ll long long

using namespace std;

int M = 1e9+7;
long long binpow(long long a, long long b){
    ios_base::sync_with_stdio(false); 
    cin.tie(NULL); cout.tie(NULL);
     if(b == 0)return 1;
    if(b%2==1)return a*binpow(a,b-1)%M;
    else{
    long long c = binpow(a,b/2);
    return c*c%M;
  }
}

long long cnr(long long a, long long b, long long m) {
    ios_base::sync_with_stdio(false); 
    cin.tie(NULL); cout.tie(NULL);
    long long t = 1, d = 1;
    
    for(int i = 1; i <= b; i ++){
        t = (t * i) % m;
        d = (d * (a - i + 1)) % m;
    }
    return ((d * binpow(t, (m - 2)))) % m;
}

int main(){
    ios_base::sync_with_stdio(false); 
    cin.tie(NULL); cout.tie(NULL);
    ll n, k;
    cin >> n >> k;
   
    if(n*binpow(2, n)/2 <=k && k <= (binpow(2, n) * n / 2) + (binpow(2, n))){ 
        k -= n*binpow(2, n)/2;
        cout << cnr(binpow(2,n), k, M); 
    }
    
    else cout << '0'; 
    return 0;
}
