#include <bits/stdc++.h>
using namespace std;

int main()
{
    list<int>lt;

    lt.push_back(100);
    lt.push_back(20);
    lt.push_back(10);
    lt.push_back(5);
    lt.push_back(1);

    int n;
    cin >> n;
    int ans = 0;

    while (n != 0) {
        ans += n / lt.front();
        n %= lt.front();
        lt.pop_front();
    }
    cout << ans << endl;
