#include <bits/stdc++.h>
#define int long long
using namespace std;
const int M = 300000;
int arr[M];
int32_t main()
{
    int n, q, m;
    scanf("%lld%lld", &n, &q);
    int s = 0;
    for (int i = 1; i <= n; i++) {
        scanf("%lld", &m);
        s += m;
        arr[i] = s;
    }
    int l, r;
    while (q--)
    {
        scanf("%lld%lld", &l, &r);
        printf("%lld\n",arr[r]-arr[l-1]);
    }
    return 0;
}
