#include <bits/stdc++.h>
#define int long long
using namespace std;
//a(n) = 1 + 2*a(floor(n/2))
int check(int n)
{
    int p = 1;
    int k = 0;
    while (n > p) {
        p *= 2;
        k++;
    }
    if (n == p) return k;
    return 0;
}
int solve(int n)
{
    if (n == 0) return 0;
    return 1 + 2 * solve(floor(n / 2.0));
}

int32_t main()
{
    int n;
    scanf("%lld", &n);
    int v = check(n+1);
    if (n == 1 || n == 3) return cout << 1, 0;
    if (v) printf("%lld", v+1);
    else printf("%lld", solve(n));
    return 0;
}
