#include <bits/stdc++.h>
#define ll long long
#define vi vector<int>
#define vii vector<pair<int, int>>
#define vll vector<ll>
#define vbl vector<bool>
#define all(x) (x).begin(), (x).end()
#define pb push_back
const int N = 222222;
using namespace std;


int main(){
  ll n, k;
  cin>>n>>k;
  
  vll v(n);
  for(int i = 0; i<n; i++){
    cin>>v[i];
  }
  
  map<ll, ll> mp;
  bool yes = 0;
  for(int i = 0; i<n-1; i++){
    if(v[i]>v[i+1]){
      mp[v[i]]++;
      if(mp[v[i]] == k){
        cout<<v[i]<<"\n";
        yes = 1;
        break;
      }
      swap(v[i], v[i+1]);
      }
    else{
      mp[v[i+1]]++;
      if(mp[v[i+1]] == k){
        cout<<v[i+1]<<"\n";
        yes = 1;
        break;
      }
    }
  }
  
  if(!yes){
    cout<<*max_element(all(v))<<"\n";
  }
  
  return 0;
}
