#include <iostream>
using namespace std;
long long t , l , r;
int main()
{
    cin >> t;
    while (t --)
    {
        cin >> l >> r;
        if (r < l * 2)
        {
            cout << r - l << endl;
        }
        else
        {
            cout << (r - 1) / 2 << endl;
        }
    }
}
