#include <bits/stdc++.h>
#define int long long
using namespace std;
bool isprime(int n, int & k) {
    for (int i = k; i * i <= n; i++) {
        if (n % i == 0) {
            k = i;
            return false;
        }
    }
    k = n;
    return true;
}
int solve() {
    int n;
    cin >> n;
    int ans = 0;
    int k = 2;
    while (n > 1) {
        if (isprime(n, k)) {
           ans++;
           break;
        }
        else {
            while (n % k == 0) {
                ans++;
                n /= k;
            }
        }
    }
    return ans * (ans + 1) / 2 - 1;
}
int32_t main()
{
    int t = 1;
    while (t--) {
        cout << solve() << endl;
    }
    return 0;
}
