#include<bits/stdc++.h>
#define INF 200102
bool P[INF];
using namespace std;
list < int > adj[INF];
void UFSUtil(int v)
{
    P[v] = true;
    for (auto i : adj[v]) if (!P[i]) UFSUtil(i);
}
void f(int n)
{
    for (int i = 0; i < n; i++) P[i] = false;
}
int solve()
{
    int n, k;
    scanf("%d%d", &n, &k);
    int u, v;
    for (int i = 0; i < k; i += 1) {
        scanf("%d%d", &u, &v);
        adj[u].push_back(v);
    }
    v = 0;
    for (int i = 0; i < n; i += 1) {
        if (!P[i]) {
            UFSUtil(i);
            v = i;
        }
    }
    f(n);
    UFSUtil(v);
    for (int i = 0; i < n; i += 1) {
        if (!P[i]) {
            return -1;
        }
    }
    return v;
}
int main()
{
    cout << solve();
    return 0;
}
