#include <bits/stdc++.h>
using namespace std;

signed main(){
  int p, d, m, s;
  cin >> p >> d >> m >> s;
  int ans = 0;
  while (1) {
    if (s >= p) {
      s -= p;
      p -= d;
      if (p < m) {
        p = m;
      }
      ans++;
    }
    else {
      break;
    }
  }
  cout << ans;
}
