using ll = long long;
int p = 1e9+7;
ll plas(ll a, ll n){
    ll s = 0;
    while(n){
        if(n & 1) s = (s + a) % p;
        n >>= 1;
        a = (a + a) % p;
    }
    return s;
}
vector<vector<ll>> mult(vector<vector<ll>> &a, vector<vector<ll>> &b){
    int n = a.size(), m = a[0].size(), k = b[0].size();
    vector<vector<ll>> r(n, vector<ll>(k));
    for(int i = 0; i < n ; i ++){
        for(int j = 0; j < k; j ++)
            for(int c = 0; c < m; c ++)
                r[i][j] = (r[i][j] + a[i][c] * b[c][j]) % p;
    }
    return r;
}
