#include<bits/stdc++.h>
#define mod 1000000007
#define int long long
using namespace std;

int solve() {
    int n;
    cin >> n;
    if (n == 1) return 0;
    int temp = 1;
    for (int i = 1; i <= n; i++) {
        int k = 1;
        if (i % 2 == 1) k = -1;
        temp = (i * temp + k) % mod;
    }
    return temp;
}

int32_t main()
{
    int t = 1;
    while (t--) cout << solve() << endl;

    return 0;
}
