#include <bits/stdc++.h>
using namespace std;
using ll = long long;
int p = 1e9+7;

int main(){
    ll t, n, k;
    cin >> t;
    while(t--){
        cin >> n >> k;
        if(n <= 4 * k) cout << n-1 << '\n';
        else{
            ll x = ceil(sqrt(1.0*n * n - 4 * n * k));
            cout << (n-x)/2 + n-(n+x+1)/2 << '\n';
        }
    }
    return 0;
