#include<bits/stdc++.h>
#define INF 22000002
#define MAX 1389264
bool prime[INF];
int PRM1[MAX];
using namespace std;
void saveprime() {
    prime[0] = prime[1] = 1;
    for (int i = 2; i * i < INF; i++) {
        if (prime[i] == 0) {
            for (int j = i * i; j < INF; j += i) {
                prime[j] = 1;
            }
        }
    }
}
int main() {
    saveprime();
    int n, k, m = 1;
    scanf("%d%d", &n, &k);
    for (int i = 2; i < INF; i++) {
        if (prime[i] == 0) {
            PRM1[m++] = i;
        }
    }
    k--;
    while (k--) {
        int mm = 1;
        for (int i = 2; i < m; i++) {
            if (prime[i] == 0) {
                PRM1[mm++] = PRM1[i];
            }
        }
        m = mm;
    }
    printf("%d", PRM1[n]);
    return 0;
}
