#include<bits/stdc++.h>
using namespace std;
bool check(string s1, string s2)
{
  for (int i = 0; i < s1.size(); i++) {
    if (s1[i] > s2[i]) {
      return true;
    }
    if (s1[i] < s2[i]) {
      return false;
    }
  }
  return true;
}
int solve() {
  string s;
  cin >> s;
  int n = s.size() - 1;
  if (n == 0) return 0;
  if (n == 1) {
    if (s[0] == '1' && s[1] == '0')
    return 0;
  }
  int ans = 0;
  for (int i = 0; i < n - 1; i++) {
    ans += (i + 1) * (i + 2) / 2;
  }
  int m = s.size();
  char arr[m];
  arr[0] = '1';
  for (int i = 1; i < m - 1; i++) {
    arr[i] = '0';
  }
  arr[m - 1] = '1';
  do
  {
    string c = "";
    for (int i = 0; i < m; i++) {
      c += arr[i];
    }
    if (check(s, c)) {
      ans++;
    }
  }
  while (next_permutation(arr, arr + m));
  char arr2[m];
  arr2[0] = '1';
  for (int i = 1; i < m - 2; i++) {
    arr2[i] = '0';
  }
  arr2[m - 2] = '1';
  arr2[m - 1] = '1';
  do
  {
    string c = "";
    for (int i = 0; i < m; i++) {
      c += arr2[i];
    }
    if (check(s, c)) {
      ans++;
    }
  }
  while (next_permutation(arr2, arr2 + m));
  return ans;
}

int main() {
   int t = 1;
   while (t--) cout << solve() << endl;
   return 0;
}
