#include <bits/stdc++.h>
#define int unsigned long long

using namespace std;

int solve(int n)
{
    if (n < 2) return 0;
    else
    if (n % 2){
        n >>= 1;
        return (n * (n + 1) / 2 + 2 * solve(n) + solve(++n));
    }
    else{
        n >>= 1;
        return (n * (n - 1) / 2 + 3 * solve(n));
    }
}

int32_t main()
{
    int n;
    cin >> n;
    cout << solve(++n);
    return 0;
}
