#include <iostream>
#include <vector>
#include <set>
using namespace std;

int pow(int q, int m) {
	if (m == 0) return 1;
	if (m == 1) return 2;
	for (int i = 1; i < m; i++)
	q *= 2;
	return q;
}

void solve() {

    int n, x, a, b;
    cin >> n;
    cin >> x;
    a = x;
    b = x;
    set < int > st;
    st.insert(x);
    for (int i = 1; i < n; i++) {
    	cin >> x;
    	a = (a ^ x);
    	b = (b & x);
    	st.insert(x);
    }
    if (n==1) {
    	cout << 0;
    	return ;
    }
    int tt = pow(2, 30) - 1;
    if (st.size() == 1 && *st.begin() == tt && n % 2 == 1) {
    	cout << 0;
    }
    else
    if (st.size() == 1 && *st.begin() == tt && n % 2 == 0) {
    	cout << 1 << "\n" << tt;
    }
    else {
    	int jj = 1;
    	while (b >= jj) jj *= 2;
    	if (a == jj) {
    		cout << 1 << "\n" << jj;
    	}
    	else{
    		cout << 2 <<"\n" << jj << " ";
    		a = (a ^ jj);
    		cout << a;
    	}
    }
}

int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int t = 1;
    while (t--) {solve();
    }

    return 0;
}
