def solve(n):

	try:
		print(f"{eval(n):.1f}",end=' ')
	except ZeroDivisionError:
		print("none", end=' ')

	n = n.replace('*', ')*(')
	n = n.replace('/', ')/(')
	n = '(' + n + ')'
	try:
		print(f"{eval(n):.1f}")
	except ZeroDivisionError:
		print("none")



n = input()
solve(n)
