#include <bits/stdc++.h>
#define int long long
using namespace std;

int f(int arr[], int n, int k) {
    int s = 0;
    for (int i = 0; i < n; i++) {
        s = s + ceil(arr[i] * 1.0 /k);
    }
    return s;
}

int solve() {
    int n, h;
    cin >> n >> h;
    int arr[n];
    for (int i = 0; i < n; i++) {
        cin >> arr[i];
    }

    int l = 1;
    int r = *max_element(arr, arr + n);
    int p = -1;

    while (l <= r) {
        int k = (l + r) / 2;
        if (f(arr, n, k) <= h) {
            p = k;
            r = k - 1;
        }
        else {
            l = k + 1;
        }
    }
    return p;
}

int32_t main()
{
    ios_base::sync_with_stdio(0);
    cin.tie(NULL);
    cout.tie(NULL);
    int t = 1;
    while (t--) cout << solve() << endl;
}
