#include <bits/stdc++.h>
#define ll long long
#define pb push_back
using namespace std;
const int N=111111;
vector<int> v[N], r;
bitset<111111> used;
int son=0;
void dfs(int s){
  if(used[s])return;
  son++;
  used[s]=1;
  for(auto u:v[s]){
    dfs(u);
  }
}


void top(){
  ll n, p;
  cin>>n>>p;
  while(p--){
    int a, b;
    cin>>a>>b;
    v[a].pb(b);
    v[b].pb(a);
  }
  
  for(int i=0;i<n;i++){
    if(!used[i]){
      son=0;
      dfs(i);
      r.pb(son);
    }
  }
  ll natija = 0, sum=0;
  for(int i=1; i<r.size(); i++){
    sum+=r[i-1];
    natija += sum*r[i];    
  }
  cout<<natija;
}
int main(){
  ios::sync_with_stdio(0);
  cin.tie(0);
  cout.tie(0);

  top();

  return 0;
}
