#include <bits/stdc++.h>
#define f(i, n) for (int i = 0; i < n; i++)
#define fr(x, i, adj) for (auto x : adj[i])
#define pb(x) push_back(x)
#define pp() pop_back()
#define st(arr, n) sort(arr, arr + n)
#define MAX 50
using namespace std;
bool P[MAX];
vector<vector<int>>tree = {{1, 3}, {0, 2, 4}, {1, 5}, {0, 4, 6}, {1, 3, 5, 7}, {2, 4, 8}, {3, 7, 9}, {4, 6, 8, 10}, {5, 7, 11}, {6, 10}, {7, 9, 11, 13}, {8, 10}, {}, {10}, {}};;
vector<int>adj[MAX], lt;
vector<pair< int, int >>pv;
string a1;
int arr1[MAX], arr2[MAX], mm, l1, l2;

void dfs(int u, int v) {

    P[u] = true;
    lt.pb(u);

    if (u == v) adj[mm++] = lt;
    else {
        fr (x, u, tree)
            if (P[x] == false)
                dfs(x, v);
    }

    lt.pp();
    P[u] = false;

}

void FIFA2022() {

    int n = 5, m = 3;
    int w[n][m], x, ans;
    f (i, n-1) f (j, m) scanf("%d", &w[i][j]);
    cin >> a1 >> x >> a1;
    w[4][1] = x;
    if (!w[0][0]) {
        dfs(2, 13); ans = w[0][2];
    }
    else {
        dfs(0, 13); ans = w[0][0];
    }
    w[0][1] -= 10; w[1][1] -= 10;
    w[1][0] -= 5;  w[1][2] -= 5;
    w[2][1] -= 5;  w[4][1] -= 15;
    f (i, n) f (j, m) pv.pb(make_pair(i, j));
    f (i, mm) {
        fr (u, i, adj)
            arr1[l1++] = w[pv[u].first][pv[u].second] - ans;
        st(arr1, l1);
        arr2[l2++] = arr1[l1 - 1];
        l1 = 0;
    }
    st(arr2, l2);
    if (arr2[0] > 0) printf("%d", ans + arr2[0]);
    else printf("GOAL");
}

int main()
{
    FIFA2022();
    return 0;
}
