def gcd(a, b):

	if b == 0: return a
	return gcd(b, a % b)
def f(n):

	if n == 3: return "1/2 1/2"
	if n == 4: return "1/3 1/3"
	if n == 5: return "1/4 2/3"
	if n == 6: return "1/5 1/5"

	ans = "1/" + str(n - 1)

	if n % 2 == 0:
		n //= 2
		i = n
	else:
		n //= 2
		i = n + 1

	while gcd(n, i) != 1:
		n -= 1
		i += 1
	return ans + ' ' + str(n) + '/' + str(i)


n = int(input())
print(f(n))
