#include <bits/stdc++.h>
#define ll long long
#define ull unsigned long long
#define pb push_back
#define pf push_front
#define ppb pop_back
#define ppf pop_front
#define ins insert
#define sz size
#define F first
#define S second
#define all(x) (x).begin(), (x).end()
#define rall(x) (x).rbegin(), (x).rend()
using namespace std;
const int N = 1e5 + 9, mod = 1e9 + 7, N2 = 2 * N;
deque<int> p;
bool a[10000009];
void solve() {
  for (int i=2; i<1e7; ++i){
    if (!a[i]){
      p.pb(i);
      for (int j=i+i; j<1e7; j+=i){
        a[j]=1;
      }
    }
  }
  ll k, ans=0;
  cin >> k;
  if (k==1) {
    cout << 1;
    return;
  }
  
  map<ll,ll> mp;
  ll s=0;
  
  while (k>1 && p.sz()){
    while (k%p.front()==0){
      k/=p.front();
      mp[p.front()]++;
      s++;
    }
    p.ppf();
  }
  
  if (k==1){
    int i;
    for (i=2; s>0; ++i){
      int n=i;
      
      for (auto &it:mp){
        while (n%it.F==0 && it.S>0){
          s--;
          n/=it.F;
          it.S--;
        }
      }
    }
    cout << i-1;
  }
  else {
    cout << k;
  }
}

signed main() {
     ios_base::sync_with_stdio(0);
     cin.tie(0);
     cout.tie(0);
     solve();
}
