#include <bits/stdc++.h>
#define INF 10001
#define int long long
using namespace std;
int dp[INF];

bool check(int n) {
    for (int i = 0; i < n/2; i++) {
        if (dp[i] != dp[n-i-1]) {
            return false;
        }
    }

    return true;
}

string solve() {
    string s;
    cin >> s;
    int n = s.size() - 1;
    for (int i = 0; i < n; i++) {
        int a1 = s[i] - 'a';
        int a2 = s[i+1] - 'a';
       dp[i] = abs(a1 - a2);
    }
    if (check(n)) return "Ajoyib satr";
    return "Oddiy satr";

}

int32_t main()
{
    ios_base::sync_with_stdio(0);
    cin.tie(NULL);
    cout.tie(NULL);
    int t;
    cin >> t;
    while (t--) cout << solve() << endl;
    return 0;
}
