from math import gcd

import sys
sys.setrecursionlimit(10**9)

def mult(A, B):
    m = 10**9+8
    return [[(A[0][0]*B[0][0] + A[0][1]*B[1][0])%m, (A[0][0]*B[0][1] + A[0][1]*B[1][1])%m],
            [(A[1][0]*B[0][0] + A[1][1]*B[1][0])%m, (A[1][0]*B[0][1] + A[1][1]*B[1][1])%m]]

def mpow(A, n):
    if n == 1: return [[1, 1], [1, 0]]
    B = mpow(A, n//2)
    B = mult(B, B)
    if n % 2 == 1:
        B = mult(B, A)
    return B


def fib(a):
    T = mpow([[1, 1], [1, 0]], a)
    return (T[0][1]%(10**9+8))
    
