#include <bits/stdc++.h>
#define int long long
using namespace std;
int32_t main()
{
    int n;
    cin >> n;
    int k = 1;
    for(int i=1; i<=n; i++){
        for(int j=1; j<=i; j++){
            if(j==i)cout << k++;
            else cout << k++ << " ";
        }
        if(i!=n){
                cout << "\n";
        }
    }
  return 0;
}
