def solve(n) :
	if (n == 0) :
		return 0

	return (1 + solve(n // 10))
	
def check(n) :
	
	l = solve(n)
	dup = n; sm = 0

	while (dup) :
		sm = sm + pow(dup % 10, l)
		dup = dup // 10
	
	return (n == sm)
	

n = int(input())
if (check(n)):
	print(sum([int(x) for x in str(n)]))
else:
	print(n)
