def f(arr, n):
	for i in range(n - 1):
		for j in range(i+1, n):
			if arr[i] == arr[j]:
				return True
	return False

def solve():

	n, m = map(int,input().split())
	arr = [input().replace(' ','') for x in range(n)]
	arr = [set(x) for x in arr]
	if f(arr, len(arr)): return 'NO'
	curr = '#'
	
	for x in arr:
		if len(x) != 1 or x == curr:
			return 'NO'
		curr = x
	
	return 'YES'


print(solve())
