#include <bits/stdc++.h>
using namespace std;
vector<int>adj[1001];
bool vis[1001], key = false;
  
void DFS(int u, int v, int d) {
  
    vis[u] = true;
  
    for (auto x : adj[u]) {
        if (vis[x] == false) {
            DFS(x, v, d+1);
        }
        else {
            if (x == v && d > 1 && d % 2) {
                key = true;
            }
        }
    }
}
  
void add(int u, int v) {
    adj[u].push_back(v);
    adj[v].push_back(u);
}
  
void null(int n) {
    for (int i = 0; i < n; i++) {
        vis[i] = false;
    }
}
  
string solve() {
  
    int n, m;
    cin >> n >> m;
    for (int i = 0; i < m; i++) {
        int u, v;
        cin >> u >> v;
        add(u-1, v-1);
    }
    for (int i = 0; i < n; i++) {
        DFS(i, i, 1);
        if (key) return "No";
        null(n);
    }
    return "Yes";
}
  
int main()
{
    ios_base::sync_with_stdio(0);
    cin.tie(NULL);
    cout.tie(NULL);
    
    int t = 1;
    while (t--) cout << solve() << endl;
    return 0;
}
