def MIN(n, a, b, c):
	x = [a, b, c]
	mx = []
	for i in range(3):
		if x[i] < n:
			mx += [x[i]]
	return max(mx)
	
def c1(s, k, n):
	s = [x for x in s]
	if k == -1:
		i = n - 1
		while (i >= 0 and s[i] == '0'):
			s[i] = '9'
			i -= 1
		if i >= 0:
			s[i] = chr(ord(s[i]) - 1)
	else:
		for i in range(n-1, -1, -1):
			d = ord(s[i])
			s[i] = str((d + k) % 10)
			k = (d + k) // 10
	return ''.join(s)
	
def solve(w):
	
	k = str(int(w) - 1)
	if w == w[::-1]: return w
	if k == k[::-1]: return k
	
	m = len(w)
	FH = w[:m//2]
	if m % 2 != 0:
		odd = w[m//2]
	SH = FH[::-1]
	R, E, L = '', '', ''
	tempFH = ''
	tempSH = ''
	if m % 2 != 0:
		E = FH + odd + SH
		if odd == '0':
			tempFH = c1(FH, -1, len(FH))
			tempSH = tempFH[::-1]
			R = tempFH + '9' + tempSH
		else:
			R = FH + chr(ord(odd) - 1) + SH
		if odd == '9':
			tempFH = c1(FH, 1, len(FH))
			tempSH = tempFH[::-1]
			L = tempFH + '0' + tempSH
		else:
			L = FH + chr(ord(odd) + 1) + SH
		
	else:
		n = len(FH)
		tempFH = FH
		E = FH + SH
		
		if FH[-1] == '0':
			tempFH = c1(FH, -1, n)
		else:
			x = ord(FH[-1]) - 1
			tempFH = tempFH[:n-1] + chr(x)
			
			
		tempSH = tempFH[::-1]
		R = tempFH + tempSH
		tempFH = FH
		
		if FH[-1] == '9':
			tempFH = c1(FH, 1, n)
		else:
			tempFH = tempFH[:n-1] + chr(ord(tempFH[n-1])+1)
	
		tempSH = tempFH[::-1]
		L = tempFH + tempSH
	return MIN(int(w), int(E), int(L), int(R))

