#include <bits/stdc++.h>
using namespace std;
int f(string X, string Y)
{
	string XY = X.append(Y);
	string YX = Y.append(X);
	return XY.compare(YX) > 0 ? 1 : 0;
}
void printLargest(vector<string> arr)
{
	sort(arr.begin(), arr.end(), f);

	for (int i = 0; i < arr.size(); i++)
		cout << arr[i];
}
int main()
{
	int n;
	cin >> n;
	vector<string> arr;
	for(int i=0;i<n;i++){
		string a;
		cin >> a;
		arr.push_back(a);
	}
	printLargest(arr);
	return 0;
}
